import React, {useEffect, useState, useCallback} from 'react'
import {Upload, message, Input, Modal} from 'antd'
import {LoadingOutlined, PlusOutlined, EyeOutlined} from '@ant-design/icons'
import config from 'src/configs'
import style from './index.module.scss'

/**@TODO gif、svga.... */

interface Props {
  value?: string
  onChange?: (value: string) => void
  canInputUrl?: boolean
  format?: (str: string) => string
}

const tips = {
  imageMustSmallerThan2M: '只能上传小于2M的文件!',
  uploadSuccess: '上传成功!',
  uploadFail: '上传失败!',
  inputPlaceholder: '输入图片地址',
}

export const ERROR_FALLBACK_BASE64 =
  'data:image/png;base64,'

const ACCEPT_FILE_TYPE = 'image/jpeg, image/png'
const RECOMMENDED_IMAGE_SIZE_TIPS = '图片最大限制为2M,建议压缩后再上传(常用工具中已提供在线压缩入口)'

export default function UploadImg({value, onChange, canInputUrl = true, format = (str: string) => str}: Props) {
  const [imageUrl, setImageUrl] = useState(value)
  const [loading, setLoading] = useState(false)
  const [previewVisible, setPreviewVisible] = useState(false)
  const formatValue = useCallback(format, [])
  // 处理异常
  const handleException = (msg: string = tips.uploadFail) => {
    message.error(msg)
    setImageUrl('')
    setLoading(false)
  }
  // 重置loading状态，处理再uploading的时候图片地址变化
  const resetLoading = () => loading && setLoading(false)
  const handleChange = ({file}: any) => {
    if (file.status === 'uploading') {
      setImageUrl('')
      setLoading(true)
    }
    if (file.status === 'done') {
      const res = file.response
      if (res.code == 0) {
        message.success(tips.uploadSuccess)
        setImageUrl(res.data.downloadUrl)
        onChange?.(res.data.downloadUrl)
      } else {
        handleException()
      }
    }
    if (file.status === 'error') handleException()
  }

  useEffect(() => {
    setImageUrl(value)
    resetLoading()
  }, [value, resetLoading])

  return (
    <>
      <section className={style.uploadBox}>
        <Upload
          name="file"
          accept={ACCEPT_FILE_TYPE}
          listType="picture-card"
          className="avatar-uploader"
          showUploadList={false}
          action={`${config.fileHost}/admin/upload/uploadFile`}
          withCredentials={true}
          onChange={handleChange}>
          {imageUrl ? (
            <img
              src={imageUrl}
              style={{width: '100%'}}
              // onError={() => {
              //   setImageUrl(ERROR_FALLBACK_BASE64)
              // }}
            />
          ) : (
            <div>{loading ? <LoadingOutlined /> : <PlusOutlined />}</div>
          )}
        </Upload>
        {imageUrl && (
          <div className={style.hoverpreview}>
            <EyeOutlined className={style.previewIcon} onClick={() => setPreviewVisible(true)} />
          </div>
        )}
      </section>
      {canInputUrl && (
        <Input
          placeholder={tips.inputPlaceholder}
          allowClear
          value={value || ''}
          onChange={(e: React.ChangeEvent<HTMLInputElement>) => {
            const value = e.target.value || ''
            resetLoading()
            setImageUrl(value)
            onChange?.(value)
          }}
        />
      )}
      <div className={style.hoverTips}>{RECOMMENDED_IMAGE_SIZE_TIPS}</div>

      <Modal
        visible={previewVisible}
        footer={null}
        onCancel={() => setPreviewVisible(false)}
        className={style.previrewModal}
        width="40%">
        <img src={imageUrl || ''} />
      </Modal>
    </>
  )
}
